# 수정예정
SELECT p.id, p.content, p.created_at, p.status FROM tbl_proposal p INNER JOIN tbl_member m ON p.member_proposal_id = m.id WHERE m.id = #{회원_아이디} ORDER BY created_at DESC;
# 수정 예정
SELECT p.id, m.name AS member_name, p.content, p.created_at, p.updated_at, p.status FROM tbl_proposal p INNER JOIN tbl_member m ON p.member_proposal_id = m.id where m.id = #{업체_아이디} ORDER BY created_at DESC LIMIT 10 OFFSET 0;
SELECT e.id AS estimate_id, e.cost, e.status, e.created_at, e.updated_at, e.active_yn, c.name AS company_name, p.id AS proposal_id, p.location, p.time, p.content FROM tbl_estimate e INNER JOIN tbl_company_info c ON e.company_info_estimate_id = c.id INNER JOIN tbl_proposal p ON e.proposal_estimate_id = p.id WHERE e.id = #{견적서_id}
